<?php	function wp_sc_boxes($atts, $content = null, $code) 	{		extract(shortcode_atts( array(			'align' => '',		), $atts));				if($align != '') { $align = ' align-'.$align; }				return '<div class="box-' . $code . $align . '"><div class="box-content">' . do_shortcode($content) . '</div><div class="clearboth"></div></div>';	}	add_shortcode('info','wp_sc_boxes');	add_shortcode('success','wp_sc_boxes');	add_shortcode('error','wp_sc_boxes');	add_shortcode('notice','wp_sc_boxes');	function wp_sc_black_box($atts, $content = null, $code) 	{		extract(shortcode_atts( array(			'title' => ''		), $atts));						$all = '';				if($title != '') { $title = '<div class="box-title"><h2>'.$title.'</h2></div>'; } else { $title = ''; $all = ' all-radius'; }				return '<div class="normal-box">'.$title.'<div class="box-content'. $all .'">' . do_shortcode($content) . '</div><div class="clearboth"></div></div>';	}	add_shortcode('box','wp_sc_black_box');?>